import axios from "axios";
import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import Detail from "../Detail/Detail";
import Clock from "../../images/clock.svg";
import { RightViewForecast, RightViewHistory } from "../Home/CityDTO";
function RightHistory(props: any) {
  const dateUnix = Math.round(new Date().getTime() / 1000);
  const [history, setHistory] = useState<RightViewHistory[]>([]);
  var days = [
    "Sunday",
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
  ];
  useEffect(() => {
    console.log("use effect");
    tempFun();
    async function tempFun() {
      let histories: RightViewHistory[] = [];
      for (let i = 0; i < 5; i++) {
        await axios
          .get(
            `https://api.openweathermap.org/data/2.5/onecall/timemachine?lat=${
              props.lat
            }&lon=${props.lon}&dt=${
              dateUnix - 10 - i * 86400
            }&appid=a9c7a346f3196bdf3072d1b5efdb5a34`
          )
          .then((response) => {
            let THistory = response.data.current;
            setHistory([
              ...history,
              {
                sunriseTime: THistory.sunrise,
                sunsetTime: THistory.sunset,
                temperature: THistory.temp,
                day: days[new Date(THistory.dt * 1000).getDay()],
              },
            ]);
          })
          .catch((error) => console.log(error));
      }
      console.log(histories);
      // setHistory(histories);
    }
  }, []);
  return (
    <div>
      {console.log(history.length)}
      <div>hello</div>
      {history.length !== 0 ? <div>history[0].temperature</div> : null}
      {history.map((day) => (
        <div key={day.day} className="RightCard">
          <div>{day.day}</div>
          <img className="lclockImgCard" src={Clock} alt="Clock" />
          <div>Sunrise Time {day.sunriseTime}</div>
          <div>Sunset Time {day.sunsetTime}</div>
        </div>
      ))}
    </div>
  );
}
export default RightHistory;
