import axios from "axios";
import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import { City } from "./CityDTO";
import Detail from "../Detail/Detail";
import Clock from "../../images/clock.svg";
interface iProps {
  city: City;
  key: string;
}
function CityCard(props: iProps) {
  console.log(props);
  const history = useHistory();
  // const[]
  const [state, setState] = useState({
    lat: 0,
    lon: 0,
  });
  const handleClick = () => {
    setState({ lat: props.city.latitude, lon: props.city.longitude });
    console.log(state);
    while (state.lat === 0) {
      if (state.lat !== 0) {
        history.push({
          pathname: "/Detail",
          state,
        });
        break;
      }
    }
  };
  return (
    <div className="cityCard" onClick={handleClick}>
      <div className="cityName">{props.city.name}</div>
      <div className="cityTime">{props.city.timestamp.toLocaleString()}</div>
      <img className="clockImgCard" src={Clock} alt="abc" />
      <div className="cityTemp">{props.city.temperature}</div>
      <div className="cityTitle">{props.city.title}</div>
    </div>
  );
}
export default CityCard;
