import axios from "axios";
import React, { useEffect, useState } from "react";
import { City, RightView, LeftViewToday } from "../Home/CityDTO";
import LeftSideView from "./LeftSideView";
// import {ReactComponent as Clock} from "./././images/clock.svg" ;
function Detail(props: any) {
  const [leftSide, setLeft] = useState<LeftViewToday>();
  // console.log(props);
  useEffect(() => {
    axios
      .get(
        `https://api.openweathermap.org/data/2.5/onecall?lat=${props.location.state.lat}&lon=${props.location.state.lon}&exclude=hourly,minutely,alerts&appid=a9c7a346f3196bdf3072d1b5efdb5a34&units=metric`
      )
      .then((response) => {
        let temp = response.data.current;
        let tempLeft: LeftViewToday = {
          temperature: 0,
          humidity: 0,
          timestamp: new Date(),
          name: "",
          title: "",
        };
        tempLeft.temperature = temp.temp;
        tempLeft.humidity = temp.humidity;
        tempLeft.name = props.location.state.city;
        tempLeft.title = temp.weather[0].main;
        tempLeft.timestamp = temp.dt;
        console.log(tempLeft);
        setLeft(tempLeft);
      })
      .catch((error) => console.log(error));
    console.log("i fire once");
  }, []);
  return (
    <div className="DetailView">
      <LeftSideView props={leftSide} />
      {/* <RightView/> */}
      {props.location.state.lon}--{props.location.state.lat}
    </div>
  );
}

export default Detail;
