import React, { useState } from "react";
import Clock from "../../images/alarm_clock.svg";
import { City, RightViewToday, LeftViewToday } from "../Home/CityDTO";
function LeftSideView(props: any) {
  console.log(props);
  var days = [
    "Sunday",
    "Monday",
    "Tuesday",
    "Wednesday",
    "Thursday",
    "Friday",
    "Saturday",
  ];
  const newDate = new Date(props.props.timestamp * 1000);
  let unix_timestamp = newDate;
  // Create a new JavaScript Date object based on the timestamp
  // multiplied by 1000 so that the argument is in milliseconds, not seconds.
  var date = newDate;
  // Hours part from the timestamp
  var hours = date.getHours();
  // Minutes part from the timestamp
  var minutes = "0" + date.getMinutes();
  // Seconds part from the timestamp
  var seconds = "0" + date.getSeconds();

  // Will display time in 10:30:23 format
  var formattedTime =
    hours + ":" + minutes.substr(-2) ;

  return (
    <div className="LeftView">
      <div className="lcityName">{props.props.name}</div>
      <img className="lclockImgCard" src={Clock} alt="Clock" />
      <div className="lcityTemp">{props.props.temperature}`C</div>
      <div className="lcityDate">
        {days[newDate.getDay()]} {newDate.getHours()}:{newDate.getMinutes()}
      </div>
      <div className="lcityTitle">{props.props.title}</div>
      <div className="lcityHumidity">{props.props.humidity}</div>
    </div>
  );
}

export default LeftSideView;
