import { type } from "node:os";
import React, { useState, useEffect } from "react";
import CityCard from "./CityCard";
import "./Home.css";
import axios from "axios";
export interface City {
  name: string;
  longitude: number;
  latitude: number;
  temperature: number;
  title: string;
  timestamp: Date;
}

function HomeLower() {
  const [cities, setCities] = useState<City[]>([]);
  const [search, setSearch] = useState<string>("");
  const allCities = require("indian-cities-json").cities;
  const [suggestedCities, setSuggestion] = useState<string[]>([]);

  useEffect(() => {
    var filter: string;
    filter = search.toUpperCase();
    setSuggestion(allCities);
    let tempCities = [];
    for (let i = 0; i < allCities.length; i++) {
      let txtValue = allCities[i].name;
      if (txtValue.toUpperCase().indexOf(filter) > -1) {
        tempCities.push(txtValue);
      }
    }
    setSuggestion(tempCities);
  }, [search, allCities]);
  function addCity(myCity:string) {
    let newCity:City;
    axios.get(`http://api.openweathermap.org/data/2.5/weather?q=${myCity}&appid=e899757c1162bf7b2b2684a0b2b8f5cc&units=metric`)
    .then(response=>{
      newCity={
        name: response.data.name,
        longitude: response.data.coord.lon,
        latitude:response.data.coord.lat,
        temperature: response.data.main.temp,
        title: response.data.weather[0].main,
        timestamp: new Date(response.data.dt*1000)
      }
      setCities((cities)=>cities.push(newCity));}
      )
    .catch(error=>console.log(error))
  }

  return (
    <div>
      <h2>Weather App</h2>
      <div id="myDropdown" className="dropdown-content">
        <input
          id="myInput"
          type="text"
          placeholder="Enter your City Name"
          onChange={(event) => setSearch(event.target.value)}
        />
        {suggestedCities.map((city) => (
          <div onClick={()=>addCity(city)}>{city}</div>
        ))}
      </div>
      <div>
        {cities.map((city) => (
          <CityCard city={city}></CityCard>
        ))}
      </div>
    </div>
  );
}

export default HomeLower;
