import axios from "axios";
import React, { useState } from "react";
import { City, RightView, LeftViewToday } from "../Home/CityDTO";
// import {ReactComponent as Clock} from "./././images/clock.svg" ;
function Detail(props: any) {
  const [leftSide, setLeft] = useState<LeftViewToday>();
  console.log(props);
  axios
    .get(
      `https://api.openweathermap.org/data/2.5/onecall?lat=${props.location.state.lat}&lon=${props.location.state.lon}&exclude=hourly,minutely,alerts&appid=e899757c1162bf7b2b2684a0b2b8f5cc&units=metric`
    )
    .then((response) => {
      let temp = response.data.current;
      let tempLeft: LeftViewToday = {
        temperature: 0,
        Humidity: 0,
        timestamp: new Date(),
        name: "",
        title: "",
      };
      tempLeft.temperature = temp.temp;
      tempLeft.Humidity = temp.humidity;
      tempLeft.name = props.location.state.city;
      tempLeft.title = temp.weather[0].main;
      tempLeft.timestamp = temp.dt;
      console.log(response);
      console.log(tempLeft)
    })
    .catch((error) => console.log(error));
  return (
    <div className="DetailView">
      <LeftSideView/>
      {/* <RightView/> */}
      {props.location.state.lon}--{props.location.state.lat}
    </div>
  );
}

export default Detail;
