import axios from "axios";
import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";
import Detail from "../Detail/Detail";
import Clock from "../../images/clock.svg";

function CityCard(props: any) {
  const history = useHistory();
  const handleClick = () => {
    let state = { lat: props.city.latitude, lon: props.city.longitude ,city:props.city.name};
    console.log(state);
    history.push({
      pathname: "/Detail",
      state,
    });
  };
  return (
    <div className="cityCard" onClick={handleClick}>
      <div className="cityName">{props.city.name}</div>
      <div className="cityTime">{props.city.timestamp.toLocaleString()}</div>
      <img className="clockImgCard" src={Clock} alt="abc" />
      <div className="cityTemp">{props.city.temperature}`C</div>
      <div className="cityTitle">{props.city.title}</div>
    </div>
  );
}
export default CityCard;
