import axios from "axios";
import React from "react";
import { useHistory } from "react-router-dom";
import { City } from "./CityDTO";
// import {ReactComponent as Clock} from "./././images/clock.svg" ;
interface iProps {
  city: City;
  key: string;
}
function CityCard(props: iProps) {
  console.log(props);
  return (
    <div className="cityCard"onClick={() => openDetailPage(props.city.latitude, props.city.longitude)}>
      <div className="cityName">{props.city.name}</div>
      <div className="cityTime">{props.city.timestamp.toLocaleString()}</div>
      <img className="clockImgCard"src={"./././images/clock.svg"} alt="abc" />
      <div className="cityTemp">{props.city.temperature}</div>
      <div className="cityTitle">{props.city.title}</div>
    </div>
  );
}
export default CityCard;

function openDetailPage(latitude: number, longitude: number): void {
  const history = useHistory();
  const handleClick = () => history.push('/goodbye');

  return (
    <button type="button" onClick={handleClick}>
      Goodbye
    </button>
  );
  axios
    .get(
      `https://api.openweathermap.org/data/2.5/onecall?lat=${latitude}&lon=${longitude}&exclude=hourly,minutely,alerts&appid=e899757c1162bf7b2b2684a0b2b8f5cc`
    )
    .then((response) => console.log(response)
    )
    .catch((error) => console.log(error));
}
