import axios from "axios";
import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { City } from "./CityDTO";
// import {ReactComponent as Clock} from "./././images/clock.svg" ;
interface iProps {
  city: City;
  key: string;
}
function CityCard(props: iProps) {
  console.log(props);
  const history = useHistory();
  const [state, setState] = useState({
    lat: 0,
    lon: 0
  });
  const handleClick = () => {
    // axios
    //   .get(
    //     `https://api.openweathermap.org/data/2.5/onecall?lat=${props.city.latitude}&lon=${props.city.longitude}&exclude=hourly,minutely,alerts&appid=e899757c1162bf7b2b2684a0b2b8f5cc`
    //   )
    //   .then((response) => console.log(response))
    //   .catch((error) => console.log(error));
    history.push({
      pathname: "/Detail",
      state
    });
  };
  return (
    <div className="cityCard" onClick={handleClick}>
      <div className="cityName">{props.city.name}</div>
      <div className="cityTime">{props.city.timestamp.toLocaleString()}</div>
      <img className="clockImgCard" src={"./././images/clock.svg"} alt="abc" />
      <div className="cityTemp">{props.city.temperature}</div>
      <div className="cityTitle">{props.city.title}</div>
    </div>
  );
}
export default CityCard;
