import React, { useState } from 'react';
import CityCard from "./CityCard";
export interface City{
  name:string,
  longitude:number,
  latitude:number,
  temperature:number,
  title:string,
  timestamp:Date
}
function HomeLower() {
  const [cities,setCities]=useState<City[]>([]);
  return <div className="">
     <h2>Weather App</h2>
     {/* alignment to center */}
      <input id="searchCity"type="text" placeholder="Enter your City Name"></input>
      <div>
        {cities.map((city:City)=>(
          <CityCard key={city}></CityCard>
        ))}
      </div>
    <CityCard/>
  </div>;
}

export default HomeLower;
